import React, { Component } from 'react';
import Counter from './Counter';

class Counters extends Component {
    state = { 
        counters:[
            {id:1 , value: 0},
            {id:2 , value: 10},
            {id:3 , value: 9},
            {id:4 , value: 5},
        ]
    }

    handleDelete = (counterId) =>{
        const counters = this.state.counters.filter(c => c.id !== counterId);
        this.setState( {counters: counters} )
        // console.log("Delete event handler called!",counterID);
    };
     
    handleReset = () => {
        console.log("RESET called!");
        const counters = this.state.counters.map(count => {
            count.value = 0;
            return count;
        });
        this.setState( {counters : counters } );
    };

    handleIncrement = (counterId) =>{
        const counters = this.state.counters.map( c => {
            c.value = c.id === counterId ? c.value + 1 : c.value;
            return c;
         });
         this.setState( {counters : counters } );
    }

    handleDecrement = (counterId) =>{
        const counters = this.state.counters.map( c => {
           c.value = c.id === counterId ? c.value - 1 : c.value;
           return c;
        });
        this.setState( {counters : counters } );
    }

    
    render() { 
        return ( 
            <div>
                <button onClick={this.handleReset} className="btn btn-primary m-2">RESET</button>
                <br/>
                { this.state.counters.map(counter => (
                    <Counter 
                        key={counter.id} 
                        counter = {counter}
                        onDelete = {this.handleDelete}
                        onIncrement = {this.handleIncrement}
                        onDecrement = {this.handleDecrement} >
                        {/* <h1>Counter #{counter.id}</h1> */}
                    </Counter> ) ) }
            </div>
             
        );
    }
}
 
export default Counters;
