import React, { Component } from 'react';

class Counter extends Component {
    state = { 
        count : 0 ,
        tags : ['Elemen1','Element2','Element3']
     }

    // instead of calling constructor function we can convert the event handler into
    // a arrow function 
    // constructor(){
    //     super();
    //     this.incrementButton = this.incrementButton.bind(this);
    // }

    returnTags(){
        if(this.state.tags.length === 0) return <span style={ {color:"red"} }>No items to show!</span>;
        return <ul>{ this.state.tags.map(tag => <li key={tag}>{tag}</li>) }</ul>;
    }

    incrementButton = () =>{
        
        // this will tell react that we have changed the state
        this.setState( { count: this.count + 1 , 
            tags : this.state.tags.concat('Element'.concat(1)) } );
        console.log("Button pressed!",this);
    }

