    }

    render() { 
        return ( 
            <React.Fragment>
                <button onClick={this.incrementButton} className="badge badge-primary m-2">Increment</button>
                <button onClick={this.decrementButton} className="badge badge-primary m-2">Decrement</button>
                {/* conditional statements */}
                { this.state.tags.length === 0 && "ERROR : " }
                { this.returnTags() }
            </React.Fragment>
         );
    }
}
 
export default Counter;
