import React, { Component } from 'react';

class Counter extends Component {
    state = { 
        count : 0 ,
        tags : ['Elemen1','Element2','Element3']
     }

    returnTags(){
        if(this.state.tags.length === 0) return <span style={ {color:"red"} }>No items to show!</span>;
        return <ul>{ this.state.tags.map(tag => <li key={tag}>{tag}</li>) }</ul>;
    }

    render() { 
        return ( 
            <React.Fragment>
                <h2>Rendering array Items as list: </h2>
                { this.returnTags() }
            </React.Fragment>
         );
    }
}
 
export default Counter;
