import React, { Component } from 'react';

class Counter extends Component {

    // instead of calling constructor function we can convert the event handler into
    // a arrow function 
    // constructor(){
    //     super();
    //     this.incrementButton = this.incrementButton.bind(this);
    // }

    decrementButton = () =>{
        this.setState( { count: this.props.counter.count - 1 } );
    }

    returnCountValue(){
        return this.props.counter.count === 0 ? 'Zero' : this.props.counter.count; 
    }

    render() { 
        return (
            <React.Fragment>
                {/* {this.props.children} */}
                <h1 className="badge m-2 badge-warning">{this.returnCountValue()}</h1>
                <button onClick={this.incrementButton} className="btn btn-secondary m-2">Increment</button>
                <button onClick={this.decrementButton} className="btn btn-secondary m-2">Decrement</button>
                <button onClick={() => this.props.onDelete(this.props.counter.id)} className="btn btn-danger m-2">Delete</button>
                <br/>
                {/* conditional statements */}
                {/* { this.state.tags.length === 0 && "ERROR : " } */}
                {/* { this.returnTags() } */}
            </React.Fragment>
         );
    }
}
 
export default Counter;
